    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        // TODO add your handling code here:
        if(!TextoNombre.getText().trim().isEmpty() && !TextoCodigo.getText().trim().isEmpty()){
            if(tran.agregarMulta(TextoNombre.getText(), 
                    Integer.parseInt(TextoCodigo.getText()),
                    Tipo.getSelectedItem().toString())){
                TextoNombre.setText("");
                TextoCodigo.setText("");
                Tipo.setSelectedIndex(0);
            }
        }else{
            javax.swing.JOptionPane.showMessageDialog(null, "ERROR: No lleno todos los campos.");
        }
    }//GEN-LAST:event_jButton5ActionPerformed

    private void TextoCodigoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_TextoCodigoKeyTyped
        // TODO add your handling code here:
        int tecla = evt.getKeyChar();
        
        boolean valido = tecla >= 48 && tecla <= 57;
        
        if(!valido){
            evt.consume();
        }
    }//GEN-LAST:event_TextoCodigoKeyTyped

