    private void TextoBCodigoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_TextoBCodigoKeyTyped
        // TODO add your handling code here:
        int tecla = evt.getKeyChar();
        
        boolean valido = tecla >= 48 && tecla <= 57;
        
        if(!valido){
            evt.consume();
        }
    }//GEN-LAST:event_TextoBCodigoKeyTyped

