    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton7ActionPerformed
        // TODO add your handling code here:
        if(!TextoBCodigo.getText().trim().equals("")){
            tran.PagoMulta(Integer.parseInt(TextoBCodigo.getText()));
            TextoBCodigo.setText("");
        }else{
            javax.swing.JOptionPane.showMessageDialog(null, "ERROR: Tiene campos vacíos.");
        }
    }//GEN-LAST:event_jButton7ActionPerformed

